import { BusinessInfo } from 'src/business-info/business-info.entity';
import { Clients } from 'src/clients/clients.entity';
import { Products } from 'src/products/products.entity';

interface Totals {
  productsTotal: number;
  tax: number;
  re: number;
  subTotal: number;
  transport: number;
  invoiceTotal: number;
}

interface InvoiceProducts extends Products {
  quantity: number;
  discount: number;
  finalPrice: number;
}
interface Data {
  products: InvoiceProducts[];
  client: Clients;
  businessInfo: BusinessInfo;
  totals: Totals;
  invoiceData: {
    date: string;
    expirationDate: string | null;
    id: string;
  };
}

const makeProductsBody = (products: InvoiceProducts[]) => {
  const body = [
    // Table Header
    [
      {
        text: 'Referencia',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
      {
        text: 'Descripción',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
      {
        text: 'Cantidad',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
      {
        text: 'Precio Unidad',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
      {
        text: '% Descuento',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
      {
        text: 'Total',
        style: ['itemsHeader', 'center'],
        fontSize: 10,
      },
    ],
  ];

  products.forEach(product => {
    body.push([
      {
        text: product.reference,
        style: ['itemSubTitle'],
        fontSize: 9,
        // @ts-ignore
        margin: [0, 5, 0, 0],
      },
      {
        text: product.description,
        style: ['itemSubTitle'],
        fontSize: 9,
        // @ts-ignore
        margin: [0, 5, 0, 0],
      },
      {
        // @ts-ignore
        text: product.quantity,
        style: ['itemNumber'],
        fontSize: 9,
        margin: [0, 5, 0, 0],
      },
      {
        text: `${product.price}€`,
        style: ['itemNumber'],
        fontSize: 9,
        // @ts-ignore
        margin: [0, 5, 0, 0],
      },
      {
        text: `${product.discount * 100}%`,
        style: ['itemNumber'],
        fontSize: 9,
        // @ts-ignore
        margin: [0, 5, 0, 0],
      },
      {
        text: `${product.finalPrice}€`,
        style: ['itemNumber'],
        fontSize: 9,
        // @ts-ignore
        margin: [0, 5, 0, 0],
      },
    ]);
  });
  return body;
};

const makeDates = invoiceData => {
  const data = [
    {
      columns: [
        {
          text: [{ text: 'Fecha de emisión: ', bold: true }, invoiceData.date],
          style: 'invoiceSubTitle',
          // width: '100',
          margin: [30, 5, 90, 0],
        },
      ],
    },
  ];
  if (invoiceData.expirationDate) {
    data.push({
      columns: [
        {
          text: [
            { text: 'Fecha de expiración: ', bold: true },
            invoiceData.expirationDate,
          ],
          style: 'invoiceSubTitle',
          //   width: '*',
          margin: [30, 5, 78, 0],
        },
      ],
    });
  }
  return data;
};

